from PyQt5.QtCore import QThread, pyqtSignal, QObjectimport serialclass DaemonThread(QThread):    fullLoadRateChange_Signal = pyqtSignal(str,str)    videoToggle_Signal = pyqtSignal(str)    resultSuccess_Sinal  = pyqtSignal()    def __init__(self):        super(DaemonThread, self).__init__()        #self.serialobj = serial.Serial("/dev/ttyAMA0",115200,timeout=0.2)//linux        self.serialobj = serial.Serial("COM7",115200,timeout=0.2)        self.thread_run = True    def run(self):        while(self.thread_run):            try:                ret  = self.serialobj.readline().decode()            except:                continue            if ret!="":                print(ret)                arr = ret.split(":")                if arr[0] == "ST" and len(arr)==4:                    self.fullLoadRateChange_Signal.emit(arr[1],arr[3])                if arr[0] == "PL" and len(arr)==3:                    self.videoToggle_Signal.emit(arr[2])                if arr[0] == "ST" and len(arr) ==2:                    self.resultSuccess_Sinal.emit()